function formatApiGwContextualInformation(correlationId, apiContext, awsContext) {
  return {
    correlationId,
    apiContext: {
      ...apiContext,
    },
    awsContext: {
      awsRequestId: awsContext.awsRequestId,
      functionName: awsContext.functionName,
      functionVersion: awsContext.functionVersion,
      logGroupName: awsContext.logGroupName,
      logStreamName: awsContext.logStreamName,
    },
  };
}

function formatLog(functionName, logType, contextualInformation) {
  return {
    functionName,
    logType,
    ...contextualInformation,
  };
}

module.exports = {
  formatApiGwContextualInformation,
  formatLog,
};
